import React, { useEffect, useState } from 'react';

const sectionsData = [
  {
    title: 'Post',
    description: [
      '100% USER CONTROL',
      'U Post to who you want.',
      'U decide how long your content lives',
      'Self-Destruct times illustration',
    ],
    image: './Images/notIcons/Post.png',
    iconDefault: './Images/icons/Post-icon.png',
    iconSelected: './Images/icons/Selected-Post.png',
  },
  {
    title: 'Chat',
    description: [
      'Encrypted for your security.',
      'Message 1 person, create a group and message more!',
    ],
    image: './Images/notIcons/Chat Images.png',
    iconDefault: './Images/icons/Chat.png',
    iconSelected: './Images/icons/Selected Chat.png',
  },
  {
    title: 'Events',
    description: [
      'Scheduling events, meetings, lunches and dinners have never been easier.',
      'Set the event, select the guest and send the event, we handle the rest.',
      'Automatically enters in your event calendar.',
    ],
    image: './Images/notIcons/Birthday.png',
    iconDefault: './Images/icons/Events.png',
    iconSelected: './Images/icons/Selected Events.png',
  },
  {
    title: 'Communities',
    description: [
      'U can set up any type of group, whether with two people or a new community for everyone.',
      'Public and private groups for you to decide to create or join.',
    ],
    image: './Images/notIcons/Cafe.png',
    iconDefault: './Images/icons/Community.png',
    iconSelected: './Images/icons/Selected Community.png',
  },
  {
    title: 'Stories',
    description: [
      'Stay connected with your college friends and share memories all at once!',
    ],
    image: './Images/notIcons/Story.png',
    iconDefault: './Images/icons/Story icon.png',
    iconSelected: './Images/icons/Selected Story.png',
  },
];

const ScrollNavigation = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY + window.innerHeight / 3;
      const sectionElements = document.querySelectorAll('.scroll-section');

      sectionElements.forEach((section, index) => {
        if (
          section.offsetTop <= scrollPosition &&
          section.offsetTop + section.offsetHeight > scrollPosition
        ) {
          setActiveIndex(index);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (index) => {
    const sectionElements = document.querySelectorAll('.scroll-section');
    if (sectionElements[index]) {
      sectionElements[index].scrollIntoView({ behavior: 'smooth' });
      setActiveIndex(index);
    }
  };

  return (
    <section className="container-fluid mb-0 position-relative">
      <div className="position-fixed button-container col-1">
        {sectionsData.map((section, index) => (
          <button
            key={index}
            className="border-0 bg-white image-button"
            onClick={() => scrollToSection(index)}
          >
            <img
              src={activeIndex === index ? section.iconSelected : section.iconDefault}
              alt={`${section.title} button`}
              className="img-fluid"
            />
          </button>
        ))}
      </div>

      {sectionsData.map((section, index) => (
        <div
          key={index}
          className="row scroll-section d-flex flex-column justify-content-center section mt-5"
        >
          <div className="col-2"></div>
          <div className="col-md-4 col-sm-4 col-8">
            <h1 className="text-success">{section.title}</h1>
            {section.description.map((line, idx) => (
              <p key={idx}>{line}</p>
            ))}
            <img src={section.image} alt={section.title} className="img-fluid" />
          </div>
        </div>
      ))}
    </section>
  );
};

export default ScrollNavigation;
